CREATE TABLE IF NOT EXISTS User(id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, login VARCHAR(30) NOT NULL UNIQUE, email VARCHAR(50) NOT NULL UNIQUE, password VARCHAR(256) NOT NULL, rating BIGINT NOT NULL DEFAULT 0);
CREATE TABLE IF NOT EXISTS Session(id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, session VARCHAR(50) NOT NULL UNIQUE, userId BIGINT NOT NULL, creationTime DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP());
CREATE TABLE IF NOT EXISTS Plane(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, name VARCHAR(20) NOT NULL UNIQUE, src VARCHAR(50) NOT NULL);
CREATE TABLE IF NOT EXISTS Airport(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, title VARCHAR(50) NOT NULL UNIQUE, src VARCHAR(50) NOT NULL);